function setCookie(e,o,t){var i=t;if(t){var n=new Date;n.setTime(n.getTime()+24*i*60*60*1e3),document.cookie=e+"="+escape(o)+";expires="+n.toGMTString()}else document.cookie=e+"="+escape(o)}function getCookie(e){for(var o,t=document.cookie.split("; "),i=0;i<t.length;i++){var n=t[i].split("=");if(e==n[0]){o=unescape(n[1]);break}o=null}return o}function delCookie(e){var o=new Date;o.setTime(o.getTime()-1e4),document.cookie=e+"=;expires="+o.toGMTString()}